import React from 'react';

const UserTable = (props) => {
    return (
        <table>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {props.users.length > 0 ? (
                    props.users.map(user => {
                        const { id, name } = user;
                        return (
                            <tr key={id}>
                                <td>{name}</td>
                                <td>
                                    <button className="button-primary" onClick={() => props.deleteUser(id)}>Delete</button>
                                    <button className="btn btn-primary" onClick={() => props.editUser(id, user)}>Update</button>
                                </td>
                            </tr>
                        )
                    })
                ) : (
                    <tr>
                        <td colSpan={4}>No users found</td>
                    </tr>
                )
                }
            </tbody>
        </table>
    )
}

export default UserTable;
